SECTION .bss
; define uninitialized data here
curr: 		RESB 1
colSum: 	RESB 1
saveECX:	RESD 1


SECTION .text
global _main
_main:

mov	ecx, ROWS
mov	eax, surprise

toploop:	;does important things outside the second loop
mov	ebx, 0	
mov	[saveECX], ecx
mov	ecx, COLS

innerloop:
;eip is segfaulting around here or before printFunc
sub	[eax], ebx
push	eax	;so you don't lose values when registers are used in printFunc
push	ebx
push	ecx


call	printFunc

pop	ecx
pop	ebx
pop	eax


inc	ebx
add	eax, 1


loop innerloop


call	skipLine
mov	ecx, [saveECX]

loop toploop



;;print return after 80 chars for new row
lastBreak:
; Normal termination code
mov eax, 1
mov ebx, 0
int 80h

;call after each char has its row num subtracted
printFunc:
mov	ecx, eax ;curr char, must be done before putting 4 into eax
push 	eax
mov	eax, 4
mov	ebx, 1
mov	edx, 1
int 	80h
pop	eax
ret 


; call at end of each row
skipLine:
push 	eax
mov	eax, 4
mov	ebx, 1
mov	ecx, skipChar 
mov	edx, lenSkipChar
int 	80h
pop	eax
ret
